import React from 'react';
import './Forms.css';

function Forms() {
    return (
        <div>
            <Contact />
            <RequestForm/>
        </div>
    )
}

export default Forms;

function Contact() {
    return (
        <div>
            <ContactInfo/>
            <ContactForm/>
        </div>
    )
}

function ContactInfo() {
    return (
        <div>
            <h2>Contact info</h2>
            <p>We appreciate your interest in our inventory, and apologize we do not have model details displaying 
                on the website at this time.</p>
            <p>Please fill form out below and our team will quickly respond, or, please call us at 765-470-4091 
                for more information.</p>
        </div>
    )
}

function ContactForm() {
    return (
        <form action="#" method="GET">
            <input type="text" placeholder="First name" required/>
            <input type="text" placeholder="Last name" required/>
            <input type="email" placeholder="Email" required/>
            <input type="tel" placeholder="Phone" />
            <input name="prefer" type="radio" id="preferEmail"/>
            <label htmlFor="preferEmail">Prefer e-mail</label>
            <input name="prefer" type="radio" id="preferPhone"/>
            <label htmlFor="preferPhone">Prefer phone</label>
            <input type="submit" value="Contact us"/>
        </form>
    )
}

class RequestForm extends React.Component {
    constructor() {
        super()
        this.state = {
            textareaValue: "Your message",
            type: '',
            modelValue: "Audi",
            firstYear: "",
            lastYear: "2019",
            modelsList: {
                Audi: ['Q5', 'A4', 'Q3', 'SQ5', 'Q7', 'Q8'],
                Nissan: ['370Z', 'Altima', 'Armada', 'Frontier', 'GT-R', 'Kicks', 'LEAF', 'Maxima'],
                Volkswagen: ['Passat', 'Tiguan', 'Polo', 'Vento', 'Ameo'],
                Mitsubishi: ['ASX', 'Pajero Sport', 'L200', 'Eclipse Cross', 'Mirage', 'Outlander']
            },
            modelTypeValue: '',
            trim: ''
        }
        this.handleTextChange = this.handleTextChange.bind(this);
    }

    handleTextChange(event) {
        this.setState({textareaValue: event.target.value});
    }

    updateType = (value) => {
        this.setState({ type: value });
    }

    updateModel = (value) => {
        this.setState({ modelValue: value });
    }

    updateFirstYear = (value) => {
        this.setState({ firstYear: value });
    }

    updateLastYear = (value) => {
        this.setState({ lastYear: value });
    }

    updateModelsList = (value) => {
        this.setState({ modelTypeValue: value });
    }

    updateTrim = (value) => {
        this.setState({ trim: value });
    }

    render() {
        const type = ["Any type", "New", "Pre-owned"];
        const model = ["Audi", "Nissan", "Volkswagen", "Mitsubishi"];

        let year = [];

        for(let i = 1999; i <= 2019; i++) {
            year.push(i);
        }

        const trim = ['DX', 'LX', 'LS', 'EX', 'GL', 'SE', 'GT'];
        color: ['White', 'Black', 'Aluminum Silver', 'Red', 'Warm Silver']

        console.log(this.state.type);
        return (
            <form action="#" method="GET">
                <p>Can't find what you are looking for?</p>
                <Select options={type} value={this.state.type} updateData={this.updateType} />
                <Select options={model} value={this.state.modelValue} updateData={this.updateModel} />
                <Select options={year} value={this.state.firstYear} updateData={this.updateFirstYear} />
                <Select options={year} value={this.state.lastYear} updateData={this.updateLastYear} />
                <Select options={this.state.modelsList[this.state.modelValue]} value={this.state.modelTypeValue} updateData={this.updateModelsList}  />
                <Select options={trim} value={this.state.trim} updateData={this.updateTrim}  />
                <textarea 
                    value={this.state.textareaValue} 
                    onChange={this.handleTextChange} 
                />
                <p>By submitting this form, you agree to be contacted with information regarding the vehicle you are
                    searching for.</p>
                <input type="submit" value="Let us find it" />
            </form>
        )
    } 
}

class Select extends React.Component {
    constructor() {
        super()
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.props.updateData(event.target.value);
    }

    render() {
        const options = this.props.options.map((elem, index) => {
            return <option value={elem} key={index}>{elem}</option>
        });

        return (
            <select value={this.props.value} onChange={this.handleChange}>{options}</select>
        )
    }




}
