import React from 'react';
import './Forms.css';

function Forms() {
    return (
        <div>
            <Contact />
            <RequestForm/>
        </div>
    )
}

export default Forms;

function Contact() {
    return (
        <div>
            <ContactInfo/>
            <ContactForm/>
        </div>
    )
}

function ContactInfo() {
    return (
        <div>
            <h2>Contact info</h2>
            <p>We appreciate your interest in our inventory, and apologize we do not have model details displaying 
                on the website at this time.</p>
            <p>Please fill form out below and our team will quickly respond, or, please call us at 765-470-4091 
                for more information.</p>
        </div>
    )
}

function ContactForm() {
    return (
        <form action="#" method="GET">
            <input type="text" placeholder="First name" required/>
            <input type="text" placeholder="Last name" required/>
            <input type="email" placeholder="Email" required/>
            <input type="tel" placeholder="Phone" />
            <input name="prefer" type="radio" id="preferEmail"/>
            <label for="preferEmail">Prefer e-mail</label>
            <input name="prefer" type="radio" id="preferPhone"/>
            <label for="preferPhone">Prefer phone</label>
            <input type="submit" value="Contact us"/>
        </form>
    )
}

class RequestForm extends React.Component {
    constructor() {
        super()
        this.state = {
            textareaValue: "Your message",
            type: ["Any type", "New", "Pre-owned"],
            typeValue: '',
            model: ["Audi", "Nissan", "Volkswagen", "Mitsubishi"],
            modelValue: '',
            year: [],
            firstYear: '',
            lastYear: '',
            modelsList: {
                audiModels: ['Q5', 'A4', 'Q3', 'SQ5', 'Q7', 'Q8'],
                nissanModels: ['370Z', 'Altima', 'Armada', 'Frontier', 'GT-R', 'Kicks', 'LEAF', 'Maxima'],
                volkswagenModel: ['Passat', 'Tiguan', 'Polo', 'Vento', 'Ameo'],
                mitsubishi: ['ASX', 'Pajero Sport', 'L200', 'Eclipse Cross', 'Mirage', 'Outlander']
            },
            movelValue: '',
            trim: ['DX', 'LX', 'LS', 'EX', 'GL', 'SE', 'GT'],
            color: ['White', 'Black', 'Aluminum Silver', 'Red', 'Warm Silver']
        }
        this.handleTextChange = this.handleTextChange.bind(this);
    }

    componentDidMount() {
        let year = [];

        for(let i = 1999; i <= 2019; i++) {
            year.push(i);
        }

        this.setState({
            typeValue: this.state.type[0],
            modelValue: this.state.model[0],
            year: [...this.state.year, ...year],
            firstYear: year[0],
            lastYear: year[year.length-1],
            modelValue: ''
        });

        console.log(this.state.year[0]);
    }

    handleTextChange(event) {
        this.setState({textareaValue: event.target.value});
    }



    render() {


        return (
            <form action="#" method="GET">
                <p>Can't find what you are looking for?</p>
                <Select options={this.state.type} value={this.state.typeValue} />
                <Select options={this.state.model} value={this.state.model} />
                <Select options={this.state.year}  value={this.state.firstYear}/>
                <span>-</span>
                <Select 
                    options={this.state.year} 
                    value={this.state.lastYear} 
                />
                <Select options={this.state.modelValue} />
                <Select options={this.state.trim} />
                <Select options={this.state.color} />
                <textarea 
                    value={this.state.textareaValue} 
                    onChange={this.handleTextChange} 
                />
                <p>By submitting this form, you agree to be contacted with information regarding the vehicle you are
                    searching for.</p>
                <input type="submit" value="Let us find it" />
            </form>
        )
    } 
}

function Select(props) {
    const options = props.options.map((elem, index) => {
        return <option value={elem} key={index}>{elem}</option>
    });
    return (
        <select value={props.value}>{options}</select>
    )
}
